pkg load iopkg load data-smoothingM = xlsread("Book1.xlsx");a1 = M(1:47,1);a2 = M(1:39,2);a3 = M(1:31,3);a4 = M(1:30,4);Y1 = [flipud(a2);a1];Y2 = [flipud(a3);a4];x1 = linspace(-length(a2),length(a1),length(Y1))';x2 = linspace(-length(a3),length(a4),length(Y2))';[SmoothY1] = regdatasmooth(x1, Y1,"relative","lambda",1e-5);[SmoothY2] = regdatasmooth(x2, Y2,"relative","lambda",1e-5);#plot3(zeros(length(x1),1),x1,SmoothY1, #      x2,zeros(length(x2),1),SmoothY2,#      zeros(length(x1),1),x1,Y1,'bs-',"markersize",3, #      x2,zeros(length(x2),1),Y2,'ms-',"markersize",3)#axis("tight", "tic");#___________________________x = linspace(-43,43,86)';y = linspace(-43,43,86)';z = SmoothY1;M = [x, y, z];M2 = M*u;u = roty(90);plot3(M(1:end,1), M(1:end,2), M(1:end,3),".m","markersize",12,      M2(1:end,1), M2(1:end,1), M2(1:end,1),".m","markersize",12)for i = 0:360  i;  endfor